import { useEffect, useState } from "react";
import { NavLink } from "react-router-dom";
import '../App.css'
import Alert from "./alert";

function Nav(props) {
    const [alert, setAlert] = useState(null);
    const [mode, setMode] = useState('dark');
    const darkIcon = "data:image/png;base64,"
    const lightIcon = "data:image/png;base64,"

    useEffect(() => {
      if(mode === 'dark') {
        document.body.style.backgroundColor = '#212529';
        document.body.style.color = '#ffffff';
      }
    }, [])
    function toggleMode() {
      if(mode === 'dark') {
        document.body.style.backgroundColor = '#ffffff';
        document.body.style.color = '#212529';
        setAlert({
          msg: "Light mode enabled !!",
          type: "success"
        })
        setMode('light');
      } else {
        document.body.style.backgroundColor = '#212529';
        document.body.style.color = '#ffffff';
        setAlert({
          msg: "Dark mode enabled !!",
          type: "success"
        })
        setMode('dark');
      }
    }
    return (
      <>
        <nav id='navBar'
          className={`navbar navbar-expand-lg bg-body-transparent navbar-${mode} bg-transparent`}
          style={{
            background: "rgba(33, 37, 41, 0.65)",
            backdropFilter: "blur(10px)",
            WebkitBackdropFilter: "blur(10px)",
            borderBottom: "1px solid rgba(255, 255, 255, 0.08)",
            boxShadow: "0 2px 12px rgba(0, 0, 0, 0.6)",
          }}
        >
          <div className="container-fluid">
            <a className="navbar-brand mx-4" to='/'>
              {props.title}
            </a>
            <button
              className="navbar-toggler"
              type="button"
              data-bs-toggle="collapse"
              data-bs-target="#navbarSupportedContent"
              aria-controls="navbarSupportedContent"
              aria-expanded="false"
              aria-label="Toggle navigation"
            >
              <span className="navbar-toggler-icon"></span>
            </button>
            <div
              className="collapse navbar-collapse"
              id="navbarSupportedContent"
            >
              <ul className="navbar-nav me-auto mb-2 mb-lg-0">
                <li className="nav-item">
                  <NavLink
                    className={({isActive}) => 'nav-link' + (isActive ? ' active' : '')}
                    aria-current="page"
                    to='/'
                  >
                    Home
                  </NavLink>
                </li>
                <li className="nav-item">
                  <NavLink
                    className={({isActive}) => 'nav-link' + (isActive ? ' active' : '')}
                    aria-current="page"
                    to='/about'
                  >
                    About
                  </NavLink>
                </li>
                <li className="nav-item">
                  <NavLink className={({isActive}) => 'nav-link' + (isActive ? ' active' : '')} to='/contact'>
                    Contact
                  </NavLink>
                </li>
              </ul>
              <div className="container-fluid d-flex justify-content-end">
                <img id='toggleBtn' style={{cursor: 'pointer'}} onClick={toggleMode} height='48' width='48' src={mode === 'dark' ? lightIcon : darkIcon} alt="external-Light-Mode-interface-linear-outline-icons-papa-vector"/>
              </div>
            </div>
          </div>
        </nav>
        <Alert mode={alert}/>
      </>
    );
}

export default Nav;